class Cart{constructor(t,s="cart",l="plus",e="minus",c="delete",a="USD"){this.items=t,this.plusClass=l,this.minusClass=e,this.deleteClass=c,this.cartClass=s,this.currency=a}goodsPlus(t){this.items[t].count++}goodsMinus(t){this.items[t].count-1==0?this.goodsDelete(t):this.items[t].count--}goodsDelete(t){delete this.items[t]}getTotal(){let t=0;for(let s in this.items)t+=this.items[s].count*this.items[s].price;return t.toFixed(2)}setTotal(t,s){let l=0;return l+=t*s,l.toFixed(2)}render(){let t=document.createElement("table");t.classList.add("cart-out__table"),t.classList.add(this.cartClass);let s=document.createElement("tbody");t.append(s);for(let t in this.items){let l=this.items[t];s.innerHTML+=`<tr class="cart__row cart-cells">\n                        <td class="cart-cells__delete cell">\n                            <button class="cell__button ${this.deleteClass}" data-articul="${t}">X</button>\n                        </td>\n                        <td class="cart-cells__img cell">\n                            <div class="cell__img">\n                                <a href="${l.image}">\n                                    <picture>\n                                        <source srcSet="${l.imageWebp}" type="image/webp" />\n                                        <img src="${l.image}" class="cell__img_img"/>\n                                    </picture>\n                                </a> \n                            </div>\n                        </td>\n                        <td class="cart-cells__name cell">\n                            <h4 class="cell__title">${l.name}</h4>\n                        </td>\n                        <td class="cart-cells__minus cell">\n                            <button class="cell__minus ${this.minusClass}" data-articul="${t}">-</button>\n                        </td>\n                        <td class="cart-cells__count cell">\n                            <span class="cell__count">${l.count}</span>\n                        </td>\n                        <td class="cart-cells__plus cell">\n                            <button class="cell__plus ${this.plusClass}" data-articul="${t}">+</button>\n                        </td>\n                        <td class="cart-cells__total cell">\n                            <span class="cell__total">${this.setTotal(l.count,l.price)} ${this.currency}</span>\n                        </td>\n                    </tr>`}return s.innerHTML+=`<tr class="cart__row cart-cells _sticky">\n                <td class="cart-cells__ftotal cell">\n                    <div class="cell__ftotal total">\n                        Total: \n                        <span class="cell__ftotal_sum">\n                            ${this.getTotal()} ${this.currency}\n                        </span>\n                    </div>\n                </td>\n            </tr>`,t}}